import React, { useEffect, useState } from "react";
import PropTypes from "prop-types";
import {
  getAllUsers,
  deleteUserById,
  addUser,
  updateUserById,
} from "../../services/ApiUser";

export default function CardTable({ color }) {
  const [users, setUsers] = useState([]);

  const getUsers = async () => {
    try {
      const response = await getAllUsers();
      setUsers(response.data);
    } catch (error) {
      console.log("Erreur lors du chargement :", error);
    }
  };

  useEffect(() => {
    getUsers();
  }, []);

  const [newUser, setNewUser] = useState({
    name: "",
    email: "",
    password: "",
    role: "client",
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setNewUser({ ...newUser, [name]: value });
  };

  const handleAddUser = async () => {
    try {
      await addUser(newUser);
      getUsers();
      resetForm();
    } catch (error) {
      console.log("Erreur lors de l’ajout :", error);
    }
  };

  const handleUpdateUser = async () => {
    try {
      await updateUserById(newUser._id, newUser);
      getUsers();
      resetForm();
    } catch (error) {
      console.log("Erreur lors de la mise à jour :", error);
    }
  };

  const handleEdit = (user) => {
    setNewUser({
      _id: user._id,
      name: user.name,
      email: user.email,
      password: "",
      role: user.role,
    });
  };

  const handleDelete = async (id) => {
    try {
      await deleteUserById(id);
      getUsers();
    } catch (error) {
      console.log("Erreur lors de la suppression :", error);
    }
  };

  const resetForm = () => {
    setNewUser({
      name: "",
      email: "",
      password: "",
      role: "client",
    });
  };

  return (
    <>
      <div
        className={
          "relative flex flex-col min-w-0 break-words w-full mb-6 shadow-lg rounded " +
          (color === "light" ? "bg-white" : "bg-lightBlue-900 text-white")
        }
      >
        <div className="rounded-t mb-0 px-4 py-3 border-0">
          <div className="flex flex-wrap items-center">
            <div className="relative w-full px-4 max-w-full flex-grow flex-1">
              <h3
                className={
                  "font-semibold text-lg " +
                  (color === "light" ? "text-blueGray-700" : "text-white")
                }
              >
                Liste des utilisateurs
              </h3>

              {/* Formulaire */}
              <div className="flex flex-wrap gap-2 mt-2">
                <input
                  type="text"
                  name="name"
                  placeholder="Nom"
                  value={newUser.name}
                  onChange={handleChange}
                  className="p-2 rounded text-black"
                />
                <input
                  type="email"
                  name="email"
                  placeholder="Email"
                  value={newUser.email}
                  onChange={handleChange}
                  className="p-2 rounded text-black"
                />
                <input
                  type="password"
                  name="password"
                  placeholder="Mot de passe"
                  value={newUser.password}
                  onChange={handleChange}
                  className="p-2 rounded text-black"
                />
                <select
                  name="role"
                  value={newUser.role}
                  onChange={handleChange}
                  className="p-2 rounded text-black"
                >
                  <option value="client">Client</option>
                  <option value="admin">Admin</option>
                  <option value="livreur">Livreur</option>
                </select>
                <button
                  onClick={handleAddUser}
                  className="bg-green-500 text-white px-4 py-2 rounded"
                >
                  Ajouter
                </button>
                {newUser._id && (
                  <button
                    onClick={handleUpdateUser}
                    className="bg-yellow-500 text-black px-4 py-2 rounded"
                  >
                    Modifier
                  </button>
                )}
              </div>
            </div>
          </div>
        </div>

        {/* Tableau */}
        <div className="block w-full overflow-x-auto mt-4">
          <table className="items-center w-full bg-transparent border-collapse">
            <thead>
              <tr>
                {["Nom", "Email", "Rôle", "Date d'inscription", "Actions"].map(
                  (header) => (
                    <th
                      key={header}
                      className={
                        "px-6 py-3 text-xs font-semibold text-left uppercase " +
                        (color === "light"
                          ? "bg-blueGray-50 text-blueGray-500"
                          : "bg-lightBlue-800 text-lightBlue-300")
                      }
                    >
                      {header}
                    </th>
                  )
                )}
              </tr>
            </thead>
            <tbody>
              {users.map((user) => (
                <tr key={user._id}>
                  <td className="px-6 py-2 border-t border-blueGray-200 text-sm">
                    {user.name}
                  </td>
                  <td className="px-6 py-2 border-t border-blueGray-200 text-sm">
                    {user.email}
                  </td>
                  <td className="px-6 py-2 border-t border-blueGray-200 text-sm">
                    {user.role}
                  </td>
                  <td className="px-6 py-2 border-t border-blueGray-200 text-sm">
                    {user.createdAt
                      ? new Date(user.createdAt).toLocaleDateString()
                      : "-"}
                  </td>
                  <td className="px-6 py-2 border-t border-blueGray-200 text-sm text-right">
                    <button
                      onClick={() => handleEdit(user)}
                      className="bg-blue-500 text-white px-3 py-1 rounded mr-2"
                    >
                      Modifier
                    </button>
                    <button
                      onClick={() => handleDelete(user._id)}
                      className="bg-red-500 text-white px-3 py-1 rounded"
                    >
                      Supprimer
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </>
  );
}

CardTable.defaultProps = {
  color: "light",
};

CardTable.propTypes = {
  color: PropTypes.oneOf(["light", "dark"]),
};
