import React, { useContext, useState } from "react";
// …

export default function Login() {
  const { lang } = useContext(LanguageContext);
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [remember, setRemember] = useState(false);

  const handleSubmit = (e) => {
    e.preventDefault();
    // appelle ton API d'authentification ici
    console.log({ email, password, remember });
  };

  return (
    <form onSubmit={handleSubmit}>
      {/* Email */}
      <div className="relative w-full mb-3">
        <label htmlFor="login-email" className="block uppercase text-black text-xs font-bold mb-2">
          {translations[lang].email}
        </label>
        <input
          id="login-email"
          type="email"
          value={email}
          onChange={e => setEmail(e.target.value)}
          placeholder={translations[lang].placeholderEmail}
          className="border …"
        />
      </div>

      {/* Mot de passe */}
      <div className="relative w-full mb-3">
        <label htmlFor="login-password" className="block uppercase text-black text-xs font-bold mb-2">
          {translations[lang].password}
        </label>
        <input
          id="login-password"
          type="password"
          value={password}
          onChange={e => setPassword(e.target.value)}
          placeholder={translations[lang].placeholderPassword}
          className="border …"
        />
      </div>

      {/* Se souvenir de moi */}
      <label className="inline-flex items-center mb-6">
        <input
          type="checkbox"
          checked={remember}
          onChange={e => setRemember(e.target.checked)}
          className="form-checkbox …"
        />
        <span className="ml-2 text-sm font-semibold text-black">
          {translations[lang].remember}
        </span>
      </label>

      {/* Bouton */}
      <button
        type="submit"
        className="w-full text-white font-semibold rounded-lg py-3 shadow-md"
      >
        {translations[lang].login}
      </button>
    </form>
  );
}
