import React, { useEffect, useState } from "react";
import {
  getAllUsers,
  addUser,
  updateUserById,
  deleteUserById,
} from "../../services/ApiUser";

export default function CardTable() {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [role, setRole] = useState("client");
  const [users, setUsers] = useState([]);
  const [selectedId, setSelectedId] = useState(null);

  useEffect(() => {
    chargerUtilisateurs();
  }, []);

  const chargerUtilisateurs = async () => {
    const res = await getAllUsers();
    setUsers(res.data);
  };

  const resetForm = () => {
    setName("");
    setEmail("");
    setPassword("");
    setRole("client");
    setSelectedId(null);
  };

  const handleSubmit = async () => {
    const userData = { name, email, password, role };
    if (selectedId) {
      await updateUserById(selectedId, userData);
    } else {
      await addUser(userData);
    }
    resetForm();
    chargerUtilisateurs();
  };

  const handleEdit = (user) => {
    setName(user.name);
    setEmail(user.email);
    setRole(user.role);
    setSelectedId(user._id);
  };

  const handleDelete = async (id) => {
    await deleteUserById(id);
    chargerUtilisateurs();
  };

  return (
    <div className="bg-blue-900 p-6 rounded-lg shadow-md text-white w-full">
      <h2 className="text-2xl font-bold mb-6 text-center">Liste des utilisateurs</h2>

      {/* Formulaire */}
      <div className="flex flex-wrap gap-4 mb-6 justify-center">
        <input
          className="p-2 rounded text-black"
          placeholder="Nom"
          value={name}
          onChange={(e) => setName(e.target.value)}
        />
        <input
          className="p-2 rounded text-black"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
        <input
          className="p-2 rounded text-black"
          placeholder="Mot de passe"
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <select
          className="p-2 rounded text-black"
          value={role}
          onChange={(e) => setRole(e.target.value)}
        >
          <option value="client">Client</option>
          <option value="admin">Admin</option>
          <option value="livreur">Livreur</option>
        </select>
        <button
          onClick={handleSubmit}
          className="bg-yellow-400 text-black px-4 py-2 rounded"
        >
          {selectedId ? "Mettre à jour" : "Ajouter"}
        </button>
      </div>

      {/* Tableau */}
      <div className="overflow-x-auto">
        <table className="min-w-full table-auto text-white">
          <thead>
            <tr className="border-b border-white text-sm text-left">
              <th className="px-4 py-2">Nom</th>
              <th className="px-4 py-2">Email</th>
              <th className="px-4 py-2">Rôle</th>
              <th className="px-4 py-2">Date inscription</th>
              <th className="px-4 py-2">Actions</th>
            </tr>
          </thead>
          <tbody>
            {users.map((user) => (
              <tr key={user._id} className="border-b border-white text-sm">
                <td className="px-4 py-2">{user.name}</td>
                <td className="px-4 py-2">{user.email}</td>
                <td className="px-4 py-2">{user.role}</td>
                <td className="px-4 py-2">
                  {user.createdAt
                    ? new Date(user.createdAt).toLocaleDateString()
                    : "-"}
                </td>
                <td className="px-4 py-2">
                  <button
                    onClick={() => handleEdit(user)}
                    className="bg-blue-500 px-3 py-1 rounded text-white mr-2"
                  >
                    Update
                  </button>
                  <button
                    onClick={() => handleDelete(user._id)}
                    className="bg-red-500 px-3 py-1 rounded text-white"
                  >
                    Delete
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}
