import React, { useEffect, useState } from "react";
import {
  getAllUsers,
  addUser,
  updateUserById,
  deleteUserById,
} from "../../services/ApiUser";

export default function CardTable() {
  const [nom, setNom] = useState("");
  const [email, setEmail] = useState("");
  const [motDePasse, setMotDePasse] = useState("");
  const [role, setRole] = useState("Etudiant");
  const [users, setUsers] = useState([]);
  const [selectedId, setSelectedId] = useState(null);

  useEffect(() => {
    chargerUtilisateurs();
  }, []);

  const chargerUtilisateurs = async () => {
    const res = await getAllUsers();
    setUsers(res.data);
  };

  const handleSubmit = async () => {
    const userData = { nom, email, motDePasse, role };
    if (selectedId) {
      await updateUserById(selectedId, userData);
    } else {
      await addUser(userData);
    }
    setNom("");
    setEmail("");
    setMotDePasse("");
    setRole("Etudiant");
    setSelectedId(null);
    chargerUtilisateurs();
  };

  const handleEdit = (user) => {
    setNom(user.nom);
    setEmail(user.email);
    setMotDePasse(""); // ne pas pré-remplir le mot de passe
    setRole(user.role);
    setSelectedId(user._id);
  };

  const handleDelete = async (id) => {
    await deleteUserById(id);
    chargerUtilisateurs();
  };

  return (
    <div className="bg-blue-900 p-6 rounded-lg shadow-md text-white">
      <h2 className="text-2xl font-bold mb-4">Liste des utilisateurs</h2>
      <div className="flex flex-wrap gap-4 mb-4">
        <input
          className="p-2 rounded text-black"
          placeholder="Nom"
          value={nom}
          onChange={(e) => setNom(e.target.value)}
        />
        <input
          className="p-2 rounded text-black"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
        <input
          className="p-2 rounded text-black"
          placeholder="Mot de passe"
          type="password"
          value={motDePasse}
          onChange={(e) => setMotDePasse(e.target.value)}
        />
        <select
          className="p-2 rounded text-black"
          value={role}
          onChange={(e) => setRole(e.target.value)}
        >
          <option value="Etudiant">Etudiant</option>
          <option value="Enseignant">Enseignant</option>
          <option value="Admin">Admin</option>
        </select>
        <button
          onClick={handleSubmit}
          className="bg-yellow-400 text-black px-4 py-2 rounded"
        >
          {selectedId ? "Mettre à jour" : "Ajouter"}
        </button>
      </div>

      <table className="w-full text-white">
        <thead>
          <tr>
            <th className="text-left py-2">Nom</th>
            <th className="text-left py-2">Email</th>
            <th className="text-left py-2">Rôle</th>
            <th className="text-left py-2">Actions</th>
          </tr>
        </thead>
        <tbody>
          {users.map((user) => (
            <tr key={user._id} className="border-t border-white">
              <td className="py-2">{user.nom}</td>
              <td className="py-2">{user.email}</td>
              <td className="py-2">{user.role}</td>
              <td className="py-2">
                <button
                  onClick={() => handleEdit(user)}
                  className="mr-2 underline text-yellow-300"
                >
                  Modifier
                </button>
                <button
                  onClick={() => handleDelete(user._id)}
                  className="underline text-red-400"
                >
                  Supprimer
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
