import React, { useContext, useState } from "react";
import { Link } from "react-router-dom";
import { LanguageContext } from "../../context/LanguageContext";
import { translations } from "../../i18n/translations";

export default function Register() {
  const { lang } = useContext(LanguageContext);

  const [nom, setNom] = useState("");
  const [email, setEmail] = useState("");
  const [motDePasse, setMotDePasse] = useState("");
  const [role, setRole] = useState("");
  const [specialite, setSpecialite] = useState("");
  const [accepteConfidentialite, setAccepteConfidentialite] = useState(false);

  const handleSubmit = (e) => {
    e.preventDefault();

    // Tu peux maintenant envoyer ces valeurs au backend
    const userData = {
      nom,
      email,
      motDePasse,
      role,
      specialite,
      accepteConfidentialite,
    };

    console.log("ðŸ§¾ DonnÃ©es Ã  envoyer :", userData);
    // Appel Ã  axios ici si tu veux : axios.post("/api/users/inscription", userData)...
  };

  return (
    <div className="container mx-auto px-4 h-full">
      <div className="flex content-center items-center justify-center h-full">
        <div className="w-full lg:w-6/12 px-4">
          <div className="relative flex flex-col min-w-0 break-words w-full mb-6 shadow-lg rounded-lg bg-white border-0">
            <div className="rounded-t mb-0 px-6 py-6">
              <div className="text-center mb-3">
                <h6 className="text-black text-xl font-bold">
                  {translations[lang].title}
                </h6>
              </div>
              <hr className="mt-6 border-b-1 border-blueGray-300" />
            </div>
            <div className="flex-auto px-4 lg:px-10 py-10 pt-0">
              <form onSubmit={handleSubmit}>
                {/* Nom */}
                <div className="relative w-full mb-3">
                  <label className="block uppercase text-black text-xs font-bold mb-2">
                    {translations[lang].name}
                  </label>
                  <input
                    type="text"
                    value={nom}
                    onChange={(e) => setNom(e.target.value)}
                    className="border border-gray-200 px-3 py-3 placeholder-gray-400 text-gray-700 bg-white rounded text-sm shadow focus:outline-none focus:ring w-full ease-linear transition-all duration-150"
                    placeholder={translations[lang].placeholderName}
                  />
                </div>

                {/* Email */}
                <div className="relative w-full mb-3">
                  <label className="block uppercase text-black text-xs font-bold mb-2">
                    {translations[lang].email}
                  </label>
                  <input
                    type="email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    className="border border-gray-200 px-3 py-3 placeholder-gray-400 text-gray-700 bg-white rounded text-sm shadow focus:outline-none focus:ring w-full ease-linear transition-all duration-150"
                    placeholder={translations[lang].placeholderEmail}
                  />
                </div>

                {/* Mot de passe */}
                <div className="relative w-full mb-3">
                  <label className="block uppercase text-black text-xs font-bold mb-2">
                    {translations[lang].password}
                  </label>
                  <input
                    type="password"
                    value={motDePasse}
                    onChange={(e) => setMotDePasse(e.target.value)}
                    className="border border-gray-200 px-3 py-3 placeholder-gray-400 text-gray-700 bg-white rounded text-sm shadow focus:outline-none focus:ring w-full ease-linear transition-all duration-150"
                    placeholder={translations[lang].placeholderPassword}
                  />
                </div>

                {/* RÃ´le */}
                <div className="relative w-full mb-3">
                  <label className="block uppercase text-black text-xs font-bold mb-2">
                    {translations[lang].role}
                  </label>
                  <select
                    className="border border-gray-400 px-3 py-3 bg-white rounded text-sm shadow focus:outline-none focus:ring w-full ease-linear transition-all duration-150"
                    value={role}
                    onChange={(e) => setRole(e.target.value)}
                    required
                  >
                    <option value="" disabled style={{ color: "#888" }}>
                      {translations[lang].chooseRole}
                    </option>
                    <option value="etudiant" style={{ color: "#111" }}>
                      {translations[lang].etudiant}
                    </option>
                    <option value="enseignant" style={{ color: "#111" }}>
                      {translations[lang].enseignant}
                    </option>
                  </select>
                </div>

                {/* SpÃ©cialitÃ© */}
                <div className="relative w-full mb-3">
                  <label className="block uppercase text-black text-xs font-bold mb-2">
                    {translations[lang].specialite}
                  </label>
                  <input
                    type="text"
                    value={specialite}
                    onChange={(e) => setSpecialite(e.target.value)}
                    className="border border-gray-200 px-3 py-3 placeholder-gray-400 text-gray-700 bg-white rounded text-sm shadow focus:outline-none focus:ring w-full ease-linear transition-all duration-150"
                    placeholder={translations[lang].placeholderSpecialite}
                  />
                </div>

                {/* Politique de confidentialitÃ© */}
                <div className="mb-4">
                  <label className="inline-flex items-center cursor-pointer">
                    <input
                      type="checkbox"
                      checked={accepteConfidentialite}
                      onChange={(e) => setAccepteConfidentialite(e.target.checked)}
                      className="form-checkbox border border-gray-200 bg-white shadow rounded text-blueGray-700 ml-1 w-5 h-5 ease-linear transition-all duration-150"
                    />
                    <span className="ml-2 text-sm font-semibold text-blueGray-600">
                      {translations[lang].accept}{" "}
                      <a href="#pablo" className="text-lightBlue-500" onClick={(e) => e.preventDefault()}>
                        {translations[lang].policy}
                      </a>
                    </span>
                  </label>
                </div>

                {/* Bouton */}
                <div className="text-center mt-6">
                  <button
                    className="w-full text-white font-semibold rounded-lg py-3 shadow-md focus:outline-none transition-all duration-150 text-base"
                    style={{ background: "linear-gradient(to bottom, #635bfa 0%, #6d38a7 100%)" }}
                    type="submit"
                  >
                    {translations[lang].create}
                  </button>
                </div>
              </form>

              {/* Liens bas de page */}
              <div className="flex flex-wrap mt-2 relative">
                <div className="w-1/2">
                  <Link to="/auth/forget" className="text-lightBlue-500">
                    <small>{translations[lang].forgot}</small>
                  </Link>
                </div>
                <div className="w-1/2 text-right">
                  <Link to="/auth/login" className="text-black">
                    <small>{translations[lang].login}</small>
                  </Link>
                </div>
              </div>

            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
