import React, { useEffect, useState } from "react";
import PropTypes from "prop-types";
import {
  getAllUsers,
  deleteUserById,
  addUser,
  updateUserById,
} from "../../services/ApiUser";

export default function CardTable({ color }) {
  const [users, setUsers] = useState([]);

  const getUsers = async () => {
    try {
      const response = await getAllUsers();
      setUsers(response.data.utilisateurs);
    } catch (error) {
      console.error("Erreur récupération :", error);
    }
  };

  useEffect(() => {
    getUsers();
  }, []);

  const [newUser, setNewUser] = useState({
    name: "",
    email: "",
    password: "",
    role: "Etudiant",
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setNewUser({ ...newUser, [name]: value });
  };

  const handleAddUser = async () => {
    try {
      await addUser(newUser);
      getUsers();
    } catch (error) {
      console.error("Erreur ajout :", error);
    }
  };

  const handleUpdateUser = async () => {
    try {
      if (!newUser._id) return;
      await updateUserById(newUser._id, newUser);
      getUsers();
    } catch (error) {
      console.error("Erreur maj :", error);
    }
  };

  const handleDeleteUser = async (id) => {
    try {
      await deleteUserById(id);
      getUsers();
    } catch (error) {
      console.error("Erreur suppression :", error);
    }
  };

  return (
    <div
      className={
        "relative flex flex-col min-w-0 break-words w-full mb-6 shadow-lg rounded " +
        (color === "light" ? "bg-white" : "bg-lightBlue-900 text-white")
      }
    >
      <div className="rounded-t mb-0 px-4 py-3 border-0">
        <h3
          className={
            "font-semibold text-lg " +
            (color === "light" ? "text-blueGray-700" : "text-white")
          }
        >
          Liste des utilisateurs
        </h3>
        <div className="flex flex-wrap mt-4">
          <input
            type="text"
            placeholder="Nom"
            name="name"
            value={newUser.name}
            onChange={handleChange}
            className="mr-2 px-3 py-2 border rounded"
          />
          <input
            type="email"
            placeholder="Email"
            name="email"
            value={newUser.email}
            onChange={handleChange}
            className="mr-2 px-3 py-2 border rounded"
          />
          <input
            type="password"
            placeholder="Mot de passe"
            name="password"
            value={newUser.password}
            onChange={handleChange}
            className="mr-2 px-3 py-2 border rounded"
          />
          <select
            name="role"
            value={newUser.role}
            onChange={handleChange}
            className="mr-2 px-3 py-2 border rounded"
          >
            <option value="Etudiant">Etudiant</option>
            <option value="Enseignant">Enseignant</option>
            <option value="Admin">Admin</option>
          </select>
          <button
            onClick={handleAddUser}
            className="bg-green-500 text-white px-4 py-2 rounded mr-2"
          >
            Ajouter
          </button>
          <button
            onClick={handleUpdateUser}
            className="bg-yellow-500 text-white px-4 py-2 rounded"
          >
            Mettre à jour
          </button>
        </div>
      </div>

      <div className="block w-full overflow-x-auto">
        <table className="items-center w-full bg-transparent border-collapse mt-4">
          <thead>
            <tr>
              {["Nom", "Email", "Rôle", "Date d'inscription", "Actions"].map(
                (header) => (
                  <th
                    key={header}
                    className="px-6 py-3 border-b text-left text-xs font-semibold text-blueGray-500"
                  >
                    {header}
                  </th>
                )
              )}
            </tr>
          </thead>
          <tbody>
            {users.map((user) => (
              <tr key={user._id}>
                <td className="px-6 py-4 border-b">{user.name}</td>
                <td className="px-6 py-4 border-b">{user.email}</td>
                <td className="px-6 py-4 border-b">{user.role}</td>
                <td className="px-6 py-4 border-b">
                  {new Date(user.createdAt).toLocaleDateString()}
                </td>
                <td className="px-6 py-4 border-b">
                  <button
                    onClick={() => setNewUser(user)}
                    className="bg-blue-500 text-white px-3 py-1 rounded mr-2"
                  >
                    Modifier
                  </button>
                  <button
                    onClick={() => handleDeleteUser(user._id)}
                    className="bg-red-500 text-white px-3 py-1 rounded"
                  >
                    Supprimer
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

CardTable.defaultProps = {
  color: "light",
};

CardTable.propTypes = {
  color: PropTypes.oneOf(["light", "dark"]),
};
