import React, { useEffect, useState } from "react";
import {
  getAllUsers,
  addUser,
  updateUserById,
  deleteUserById,
} from "../../services/ApiUser";

export default function CardTable() {
  const [nom, setNom] = useState("");
  const [email, setEmail] = useState("");
  const [motDePasse, setMotDePasse] = useState("");
  const [role, setRole] = useState("client");
  const [telephone, setTelephone] = useState("");
  const [adresse, setAdresse] = useState("");
  const [users, setUsers] = useState([]);
  const [selectedId, setSelectedId] = useState(null);

  useEffect(() => {
    chargerUtilisateurs();
  }, []);

  const chargerUtilisateurs = async () => {
    const res = await getAllUsers();
    setUsers(res.data);
  };

  const handleSubmit = async () => {
    const userData = { nom, email, motDePasse, role, telephone, adresse };
    if (selectedId) {
      await updateUserById(selectedId, userData);
    } else {
      await addUser(userData);
    }
    resetForm();
    chargerUtilisateurs();
  };

  const resetForm = () => {
    setNom("");
    setEmail("");
    setMotDePasse("");
    setRole("client");
    setTelephone("");
    setAdresse("");
    setSelectedId(null);
  };

  const handleEdit = (user) => {
    setNom(user.nom);
    setEmail(user.email);
    setRole(user.role);
    setTelephone(user.telephone || "");
    setAdresse(user.adresse || "");
    setSelectedId(user._id);
  };

  const handleDelete = async (id) => {
    await deleteUserById(id);
    chargerUtilisateurs();
  };

  return (
    <div className="bg-blue-900 p-6 rounded-lg shadow-md text-white w-full">
      <h2 className="text-2xl font-bold mb-6">List Users</h2>

      {/* Formulaire */}
      <div className="flex flex-wrap gap-4 mb-6 justify-center">
        <input
          className="p-2 rounded text-black"
          placeholder="Nom"
          value={nom}
          onChange={(e) => setNom(e.target.value)}
        />
        <input
          className="p-2 rounded text-black"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
        <input
          className="p-2 rounded text-black"
          placeholder="Mot de passe"
          type="password"
          value={motDePasse}
          onChange={(e) => setMotDePasse(e.target.value)}
        />
        <input
          className="p-2 rounded text-black"
          placeholder="Téléphone"
          value={telephone}
          onChange={(e) => setTelephone(e.target.value)}
        />
        <input
          className="p-2 rounded text-black"
          placeholder="Adresse"
          value={adresse}
          onChange={(e) => setAdresse(e.target.value)}
        />
        <select
          className="p-2 rounded text-black"
          value={role}
          onChange={(e) => setRole(e.target.value)}
        >
          <option value="client">Client</option>
          <option value="admin">Admin</option>
          <option value="livreur">Livreur</option>
        </select>
        <button
          onClick={handleSubmit}
          className="bg-yellow-400 text-black px-4 py-2 rounded"
        >
          {selectedId ? "Update User" : "Add User"}
        </button>
      </div>

      {/* Tableau */}
      <div className="overflow-x-auto">
        <table className="min-w-full table-auto text-white">
          <thead>
            <tr className="border-b border-white">
              <th className="px-4 py-2 text-left">Username</th>
              <th className="px-4 py-2 text-left">Email</th>
              <th className="px-4 py-2 text-left">Phone</th>
              <th className="px-4 py-2 text-left">Delivery Address</th>
              <th className="px-4 py-2 text-left">Role</th>
              <th className="px-4 py-2 text-left">Date d'inscription</th>
              <th className="px-4 py-2 text-left">Actions</th>
            </tr>
          </thead>
          <tbody>
            {users.map((user) => (
              <tr key={user._id} className="border-b border-white text-sm">
                <td className="px-4 py-2">{user.nom}</td>
                <td className="px-4 py-2">{user.email}</td>
                <td className="px-4 py-2">{user.telephone || "-"}</td>
                <td className="px-4 py-2">{user.adresse || "-"}</td>
                <td className="px-4 py-2">{user.role}</td>
                <td className="px-4 py-2">
                  {new Date(user.createdAt).toLocaleDateString()}
                </td>
                <td className="px-4 py-2">
                  <button
                    onClick={() => handleEdit(user)}
                    className="bg-blue-500 px-3 py-1 rounded text-white mr-2"
                  >
                    Update
                  </button>
                  <button
                    onClick={() => handleDelete(user._id)}
                    className="bg-red-500 px-3 py-1 rounded text-white"
                  >
                    Delete
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}
