import React, { useContext, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import { LanguageContext } from "../../context/LanguageContext";
import { translations } from "../../i18n/translations";
import axios from "axios";

export default function Register() {
  const { lang } = useContext(LanguageContext);
  const navigate = useNavigate();

  // États
  const [nom, setNom] = useState("");
  const [email, setEmail] = useState("");
  const [motDePasse, setMotDePasse] = useState("");
  const [showPassword, setShowPassword] = useState(false);
  const [role, setRole] = useState("");
  const [specialite, setSpecialite] = useState("");
  const [accepteConfidentialite, setAccepteConfidentialite] = useState(false);

  // Soumission du formulaire
  const handleSubmit = async (e) => {
    e.preventDefault();

    const userData = { nom, email, motDePasse, role, specialite, accepteConfidentialite };

    try {
      const response = await axios.post("http://localhost:5000/api/users/inscription", userData);
      console.log("✅ Utilisateur inscrit :", response.data);
      alert("Inscription réussie !");
      navigate("/auth/login"); // Redirection vers login après inscription
    } catch (error) {
      console.error("❌ Erreur lors de l'inscription :", error);
      alert("Erreur lors de l'inscription.");
    }
  };

  return (
    <div className="container mx-auto px-4 h-full">
      <div className="flex content-center items-center justify-center h-full">
        <div className="w-full lg:w-6/12 px-4">
          <div className="relative flex flex-col break-words w-full mb-6 shadow-lg rounded-lg bg-white border-0">
            <div className="rounded-t px-6 py-6">
              <div className="text-center mb-3">
                <h6 className="text-black text-xl font-bold">
                  {translations[lang].title}
                </h6>
              </div>
              <hr className="mt-6 border-b-1 border-blueGray-300" />
            </div>
            <div className="flex-auto px-4 lg:px-10 py-10 pt-0">
              <form onSubmit={handleSubmit}>
                {/* Nom */}
                <div className="relative w-full mb-3">
                  <label className="block uppercase text-black text-xs font-bold mb-2">
                    {translations[lang].name}
                  </label>
                  <input
                    type="text"
                    value={nom}
                    onChange={(e) => setNom(e.target.value)}
                    placeholder={translations[lang].placeholderName}
                    className="border px-3 py-3 rounded text-sm shadow w-full focus:outline-none focus:ring transition-all duration-150"
                    required
                  />
                </div>

                {/* Email */}
                <div className="relative w-full mb-3">
                  <label className="block uppercase text-black text-xs font-bold mb-2">
                    {translations[lang].email}
                  </label>
                  <input
                    type="email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    placeholder={translations[lang].placeholderEmail}
                    className="border px-3 py-3 rounded text-sm shadow w-full focus:outline-none focus:ring transition-all duration-150"
                    required
                  />
                </div>

                {/* Mot de passe avec œil intégré dans l'input */}
                <div className="relative w-full mb-3">
                  <label className="block uppercase text-black text-xs font-bold mb-2">
                    {translations[lang].password}
                  </label>
                  <div className="relative">
                    <input
                      type={showPassword ? "text" : "password"}
                      value={motDePasse}
                      onChange={(e) => setMotDePasse(e.target.value)}
                      placeholder={translations[lang].placeholderPassword}
                      className="border px-3 py-3 pr-10 rounded text-sm shadow w-full focus:outline-none focus:ring transition-all duration-150"
                      required
                    />
                    <button
                      type="button"
                      onClick={() => setShowPassword(!showPassword)}
                      className="absolute right-3 top-1/2 -translate-y-1/2 text-black"
                      tabIndex={-1}
                      style={{ background: "none", border: "none", padding: 0 }}
                    >
                      {showPassword ? (
                        <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M13.875 18.825A10.05 10.05 0 0112 19c-5.523 0-10-4.03-10-9s4.477-9 10-9a9.964 9.964 0 018.688 5.047M15 12a3 3 0 11-6 0 3 3 0 016 0z" />
                        </svg>
                      ) : (
                        <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 3l18 18M10.477 10.477A3 3 0 0115 12m-1.879-1.879A3 3 0 0012 9m-7.036 4a9.967 9.967 0 0016.07-4.243M9.88 9.88A3 3 0 006 12" />
                        </svg>
                      )}
                    </button>
                  </div>
                </div>

                {/* Rôle */}
                <div className="relative w-full mb-3">
                  <label className="block uppercase text-black text-xs font-bold mb-2">
                    {translations[lang].role}
                  </label>
                  <select
                    value={role}
                    onChange={(e) => setRole(e.target.value)}
                    required
                    className={`border px-3 py-3 rounded text-sm shadow w-full focus:outline-none focus:ring transition-all duration-150 ${role ? "text-black" : "text-gray-400"}`}
                  >
                    <option value="" disabled>
                      {translations[lang].chooseRole}
                    </option>
                    <option value="etudiant">{translations[lang].etudiant}</option>
                    <option value="enseignant">{translations[lang].enseignant}</option>
                  </select>
                </div>

                {/* Spécialité */}
                <div className="relative w-full mb-3">
                  <label className="block uppercase text-black text-xs font-bold mb-2">
                    {translations[lang].specialite}
                  </label>
                  <input
                    type="text"
                    value={specialite}
                    onChange={(e) => setSpecialite(e.target.value)}
                    placeholder={translations[lang].placeholderSpecialite}
                    className="border px-3 py-3 rounded text-sm shadow w-full focus:outline-none focus:ring transition-all duration-150"
                    required
                  />
                </div>

                {/* Politique de confidentialité */}
                <div className="mb-4">
                  <label className="inline-flex items-center cursor-pointer">
                    <input
                      type="checkbox"
                      checked={accepteConfidentialite}
                      onChange={(e) => setAccepteConfidentialite(e.target.checked)}
                      className="form-checkbox border bg-white rounded ml-1 w-5 h-5 focus:outline-none transition-all duration-150"
                      required
                    />
                    <span className="ml-2 text-sm font-semibold text-blueGray-600">
                      {translations[lang].accept}{" "}
                      <a href="#!" className="text-lightBlue-500">{translations[lang].policy}</a>
                    </span>
                  </label>
                </div>

                {/* Bouton Envoyer */}
                <div className="text-center mt-6">
                  <button
                    type="submit"
                    className="w-full text-white font-semibold rounded-lg py-3 shadow-md focus:outline-none transition-all duration-150 text-base"
                    style={{ background: "linear-gradient(to bottom, #635bfa 0%, #6d38a7 100%)" }}
                  >
                    {translations[lang].create}
                  </button>
                </div>
              </form>

              {/* Liens bas de page */}
              <div className="flex flex-wrap mt-2">
                <div className="w-1/2">
                  <Link to="/auth/forget" className="text-lightBlue-500">
                    <small>{translations[lang].forgot}</small>
                  </Link>
                </div>
                <div className="w-1/2 text-right">
                  <Link to="/auth/login" className="text-black">
                    <small>{translations[lang].login}</small>
                  </Link>
                </div>
              </div>

            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
