import React, { useEffect, useState } from "react";
import {
  getAllUsers,
  deleteUserById,
  addUser,
  updateUserById,
} from "../../services/ApiUser";

export default function CardTable() {
  const [users, setUsers] = useState([]);
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    password: "",
    role: "client",
  });
  const [selectedId, setSelectedId] = useState(null);

  useEffect(() => {
    fetchUsers();
  }, []);

  const fetchUsers = async () => {
    const res = await getAllUsers();
    setUsers(res.data);
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleAdd = async () => {
    await addUser(formData);
    setFormData({ name: "", email: "", password: "", role: "client" });
    fetchUsers();
  };

  const handleUpdate = async () => {
    if (!selectedId) return;
    await updateUserById(selectedId, formData);
    setFormData({ name: "", email: "", password: "", role: "client" });
    setSelectedId(null);
    fetchUsers();
  };

  const handleEdit = (user) => {
    setFormData({
      name: user.name,
      email: user.email,
      password: "",
      role: user.role,
    });
    setSelectedId(user._id);
  };

  const handleDelete = async (id) => {
    await deleteUserById(id);
    fetchUsers();
  };

  return (
    <div className="bg-blue-900 text-white p-6 rounded-md shadow-md">
      <h2 className="text-xl font-bold mb-4">Liste des utilisateurs</h2>
      <div className="flex flex-wrap gap-2 mb-4">
        <input
          type="text"
          name="name"
          placeholder="Nom"
          value={formData.name}
          onChange={handleChange}
          className="p-2 rounded text-black"
        />
        <input
          type="email"
          name="email"
          placeholder="Email"
          value={formData.email}
          onChange={handleChange}
          className="p-2 rounded text-black"
        />
        <input
          type="password"
          name="password"
          placeholder="Mot de passe"
          value={formData.password}
          onChange={handleChange}
          className="p-2 rounded text-black"
        />
        <select
          name="role"
          value={formData.role}
          onChange={handleChange}
          className="p-2 rounded text-black"
        >
          <option value="client">Client</option>
          <option value="admin">Admin</option>
          <option value="livreur">Livreur</option>
        </select>
        <button
          onClick={handleAdd}
          className="bg-yellow-400 text-black px-4 py-2 rounded"
        >
          Ajouter
        </button>
        <button
          onClick={handleUpdate}
          className="bg-yellow-400 text-black px-4 py-2 rounded"
        >
          Mettre à jour
        </button>
      </div>

      <table className="w-full text-white">
        <thead>
          <tr className="border-b border-white text-sm">
            <th className="py-2 text-left">Nom</th>
            <th className="py-2 text-left">Email</th>
            <th className="py-2 text-left">Rôle</th>
            <th className="py-2 text-left">Date d'inscription</th>
            <th className="py-2 text-left">Actions</th>
          </tr>
        </thead>
        <tbody>
          {users.map((user) => (
            <tr key={user._id} className="border-b border-white text-sm">
              <td className="py-2">{user.name}</td>
              <td className="py-2">{user.email}</td>
              <td className="py-2">{user.role}</td>
              <td className="py-2">
                {user.createdAt
                  ? new Date(user.createdAt).toLocaleDateString()
                  : "-"}
              </td>
              <td className="py-2">
                <button
                  onClick={() => handleEdit(user)}
                  className="underline text-yellow-300 mr-2"
                >
                  Modifier
                </button>
                <button
                  onClick={() => handleDelete(user._id)}
                  className="underline text-red-300"
                >
                  Supprimer
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
